COPY (SELECT cid as num_of_tasks, gid as uuid, L.json->'properties'->>'name' as name FROM (SELECT COUNT(tid) as cid, gid FROM (SELECT t.json->>'identifier' as tid, t.json->>'groupIdentifier' as gid  FROM core.task AS t WHERE t.json->>'status' != 'Cancelled') AS A GROUP BY gid ORDER BY gid) AS B, core.location AS L WHERE L.json->>'id' = B.gid ORDER BY name) TO STDOUT WITH CSV HEADER;
